import React, { Component } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
//  import "bootswatch/journal/bootstrap.css";
//import { Navbar, NavItem, Nav, Grid, Row, Col } from "react-bootstrap";

const PLACES = [
  { name: "Palo Alto", zip: "94303" },
  { name: "San Jose", zip: "94088" },
  { name: "Santa Cruz", zip: "95062" },
  { name: "Honolulu", zip: "96803" }
];

class WeatherDisplay extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null
    };
  }
  componentDidMount() {
    const zip = this.props.zip;
    const URL = "http://api.openweathermap.org/data/2.5/weather?q=" +
      zip +
      "&appid=b1b35bba8b434a28a0be2a3e1071ae5b&units=imperial";
    fetch(URL).then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });
  }
  render() {
    const weatherData = this.state.weatherData;
    if (!weatherData) return <div>Loading</div>;
    const weather = weatherData.weather[0];
    const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";
    return (
      <div class="weather_info">
        <h1>
          <span id="text_weather">
          {weather.main} in {weatherData.name}
          <img src={iconUrl} alt={weatherData.description} />
          </span>
        </h1>
        <div>
        <p><span id="text" style={{marginRight: 20}}>Current:</span> {weatherData.main.temp}°</p>
        <p><span id="text" style={{marginRight: 20}}>High: </span>{weatherData.main.temp_max}°</p>
        <p><span id="text" style={{marginRight: 50}}>Low: </span>{weatherData.main.temp_min}°</p>
        <p><span id="text" style={{marginRight: 20}}>Wind Speed: </span>{weatherData.wind.speed} m/hr</p>
        </div>
      </div>
    );
  }
}
function City()
{
  return <h2>{"Choose City"}</h2>;
}

function Me()
{
  return <code>{"Rychkova Dasha"}</code>;
}


class App extends Component {
  constructor() {
    super();
    this.state = {
      activePlace: 0
    };
  }
  render() {
    const activePlace = this.state.activePlace;
    return (
      <div class = "AllApp">
      <div className="App">
        <div>
        <span id="text_weather"><City/></span>
          <ul class = "list" type = "none">
            {PLACES.map((place, index)=>(
            <li>
              <button class = "buttons" key = {index} onClick = {() => {this.setState({activePlace: index});}}>
              {place.name}
              </button>
            </li>))}
          </ul>
        </div>
        <WeatherDisplay key = {activePlace} zip = {PLACES[activePlace].zip}/>
        </div>
        <Me/>
        </div>
    );
  }
}

export default App;
